# Приводим список к типу NumPy array
from scipy.signal import butter, filtfilt

def butter_lowpass_filter(data, cutoff_freq, fs, order=5):
    nyq = 0.5 * fs  # Найквистова частота
    normal_cutoff = cutoff_freq / nyq
    b, a = butter(order, normal_cutoff, btype='low', analog=False)
    y = filtfilt(b, a, data)
    return y

cutoff_freq = 30  # Максимальная пропускаемая частота (Гц)
fs = 125          # Частота дискретизации (Гц)
order = 5

ecg_signal = butter_lowpass_filter(ecg_signal, cutoff_freq, fs, order)
# Допустим, у тебя есть сигнал ЭКГ
# Частота дискретизации (Гц)
sampling_frequency = 125

# Количество отсчётов
num_samples = len(ecg_signal)

# Временная шкала (секунды)
time_seconds = np.linspace(0, (num_samples - 1) / sampling_frequency, num_samples)

# График
plt.figure(figsize=(10, 6))
plt.plot(time_seconds, ecg_signal, color='blue', linewidth=1.5)
plt.title('ЭКГ кардиограмма')
plt.xlabel('Время (с)')
plt.ylabel('Амплитуда')
plt.grid(True)
plt.show()


